<template>
  <div class="card" @click="selectCard">
    <div v-if="visible" class="card-face is-front">
      <img :src="`../assets/peek/${value}.png`" :alt="value" />
    </div>
    <div v-else class="card-face is-back"></div>
  </div>
</template>

<script>
export default {
  props: {
    value: {
      type: String,
      required: true,
    },
    visible: {
      type: Boolean,
      default: false,
    },
    position: {
      type: Number,
      required: true,
    },
    matched: {
      type: Boolean,
      default: false,
    },
  },
  setup(props, context) {
    const selectCard = () => {
      context.emit("select-card", {
        position: props.position,
        faceValue: props.value,
      });
    };

    return { selectCard };
  },
};
</script>

<style>
.card {
  position: relative;
}

.card-face {
  width: 100%;
  height: 100%;
  position: absolute;
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.card-face.is-front {
  background-image: url("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRd-1dRA8AQGiLZnEDe_cMlc4OMqU3jng6oag&usqp=CAU");
  color: white;
}

.card-face.is-back {
  background-image: url("data:image/jpeg;base64,");
  color: white;
}
</style>
