<template>
  <div>
    <div id="bag" :class="{ burst: this.endend }"></div>
    <div id="bag-health">
      <div :style="{ width: this.health + '%' }"></div>
    </div>
    <div id="controls">
      <button @click="feed()" v-show="!this.endend">Feed</button>
      <button @click="restart()">Restart</button>
    </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      health: 100,
      endend: false,
    };
  },
  methods: {
    feed() {
      this.health -= 10;

      if (this.health <= 0) {
        this.endend = true;
      }
    },
    restart() {
      this.health = 100;
      this.endend = false;
    },
  },
};
</script>

